INSERT pregunta values(597,'¿Qué es la libertad de elección?',1,'activo','developer','2023-01-10','developer','2023-01-10',1,26,31,1);
INSERT pregunta values(598,'¿Conoces los Choice Awards? En estos premios, muchas veces dirigidos a niños  y jóvenes, no hay un jurado especialista, sino que el público elige con sus votos a los ganadores. ¿Por qué es una buena opción para otorgar premios?',1,'activo','developer','2023-01-11','developer','2023-01-11',1,6,25,1);
