INSERT pregunta values(601,'Elige la opción que explique qué es una narración.',1,'activo','developer','2023-01-13','developer','2023-01-13',1,9,49,1);
INSERT pregunta values(602,'Lee el texto que se presenta a continuación. Complétalo eligiendo la opción que contenga el verbo en pretérito perfecto compuesto. \n  \n Desde hace muchos siglos, las narraciones exponen la forma de pensar de los pueblos, sus creencias y la manera como ven el mundo. Por ello, __________ fundamentales para la humanidad.',1,'activo','developer','2023-01-14','developer','2023-01-14',1,10,59,1);
INSERT pregunta values(606,'Elige la respuesta que contenga todos los elementos de la narración.',1,'activo','developer','2023-01-15','developer','2023-01-15',1,9,49,1);
INSERT pregunta values(608,'Además de las grabaciones que los investigadores de la UNESCO recaban, ¿qué otra idea creativa podría ayudar a conservar las narraciones orales?',1,'activo','developer','2023-01-16','developer','2023-01-16',1,6,24,1);
INSERT pregunta values(611,'¿Qué tipo de texto literario es éste y por qué?',1,'activo','developer','2023-01-17','developer','2023-01-17',1,7,35,1);
INSERT pregunta values(612,'De acuerdo con el tema y con el tono de este texto, ¿cuál es su género y por qué?',1,'activo','developer','2023-01-18','developer','2023-01-18',1,7,36,1);
INSERT pregunta values(613,'El evento en torno al cual gira la obra y que le da título llega hasta el Acto ¿Cuál es?',1,'activo','developer','2023-01-19','developer','2023-01-19',1,7,35,1);
INSERT pregunta values(614,'¿En dónde trabaja la mamá de Pedro?',1,'activo','developer','2023-01-20','developer','2023-01-20',1,11,64,1);
INSERT pregunta values(615,'¿Cuáles son los vínculos entre el Acto 1 y el Acto 2?',1,'activo','developer','2023-01-21','developer','2023-01-21',1,7,35,1);
INSERT pregunta values(616,'Si quisieras montar esta obra de teatro en tu escuela, pero te piden que sea más corta, tendrías que hacer una adaptación. ¿Cuál de las siguientes propuestas de adaptación es más conveniente, teniendo en cuenta los siguientes argumentos?',1,'activo','developer','2023-01-22','developer','2023-01-22',1,6,24,1);
INSERT pregunta values(617,'¿Qué es el reciclaje?',1,'activo','developer','2023-01-23','developer','2023-01-23',1,26,31,1);
