INSERT pregunta values(622,'¿Qué es la diversidad?',1,'activo','developer','2023-01-26','developer','2023-01-26',1,9,49,1);
INSERT pregunta values(623,'Lee la descripción que se presenta a continuación y elige el personaje al que se refiere. \n  \n Científico matemático inglés conocido como el padre de la informática, visionario de la inteligencia artificial, el cual salvó muchas vidas en la Segunda Guerra Mundial con su compleja maquina Enigma.',1,'activo','developer','2023-01-27','developer','2023-01-27',1,9,48,1);
